import React, { useState } from "react";
import { TextInputField, Button, Pane } from "evergreen-ui";

export default function SignUp() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [passwordRepeated, setPasswordRepeated] = useState("");
  const [showErrors, setShowErrors] = useState(false);

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexDirection: "column"
      }}
    >
      <Pane
        width={400}
        display="flex"
        alignItems="center"
        justifyContent="center"
        border="default"
        padding={15}
      >
        <form
          style={{ width: 250 }}
          onSubmit={e => {
            e.preventDefault();
          }}
        >
          <TextInputField
            required
            label="Username"
            value={username}
            onChange={v => {
              setUsername(v.target.value);
            }}
          />
          <TextInputField
            required
            type="password"
            label="Password"
            value={password}
            onChange={v => {
              setPassword(v.target.value);
            }}
          />
          <TextInputField
            required
            isInvalid={
              showErrors && passwordRepeated && passwordRepeated !== password
            }
            validationMessage={
              showErrors && passwordRepeated && passwordRepeated !== password
                ? "The passwords don't match"
                : undefined
            }
            type="password"
            label="Confirm Password"
            value={passwordRepeated}
            onChange={v => {
              setPasswordRepeated(v.target.value);
            }}
          />
          <div>
            <Button
              onClick={() => {
                setShowErrors(true);
              }}
              disabled={!(Boolean(username) && Boolean(password))}
              appearance="primary"
              type="submit"
            >
              Sign Up
            </Button>
          </div>
        </form>
      </Pane>
    </div>
  );
}
